mapper.select:
SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, ROLE, JOIN_DT
FROM MEMBERS
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, ROLE, JOIN_DT
FROM MEMBERS
WHERE MEMBER_ID = #{memberId}
  AND MEMBER_PW = #{memberPw}

mapper.update:
UPDATE MEMBERS
SET
    MEMBER_PW = #{memberPw, jdbcType=VARCHAR},
    MEMBER_NAME = #{memberName, jdbcType=VARCHAR},
    ROLE = #{role, jdbcType=VARCHAR}
WHERE MEMBER_ID = #{memberId, jdbcType=VARCHAR}
